import { INC_COUNT, DEC_COUNT, DISPLAY } from "./constants";
const initialState = 0;

const changeCount = (state = initialState, action) => {
    switch (action.type) {
      
    case INC_COUNT:
      return state + 1;
    case DEC_COUNT:
      return state - 1;
    case DISPLAY:
      return action.name;
    default:
      return state;
  }
};

export default changeCount;
